ENABLE_EMAIL_NOTIFICATIONS.source_SQL:
(
	SELECT 'general' AS ID
		, 'General' AS name
) UNION (
	SELECT 'admin' AS ID
		, 'Area Admin' AS name
) UNION (
	SELECT 'restricted' AS ID
		, 'Area Restricted' AS name
) UNION (
	SELECT 'manage' AS ID
		, 'Area Manage' AS name
)

